package ch01;

public class StringOpExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a =10;
		a+=5;//15
		
		//曼炼鸥涝
		String str = "hello";
		
		str += "hi"; //"hellohi" 按眉积己, "hi"按眉积己
		str += "bye";//"hellohibye" 按眉积己, "bye"按眉积己
		System.out.println(str);
				
	}

}
